    }

    .wikitable {
      background-color: @base;
    }

    .wikitable > tr > th,
    .wikitable > tr > td,
    .wikitable > * > tr > th,
    .wikitable > * > tr > td {
      color: @text;
      border-color: @crust;
    }

    .wikitable > tr > th,
    .wikitable > * > tr > th {
      background-color: @mantle;
      color: @text;
    }

    .pre {
      color: @mantle;
    }

    .mw-headline {
      color: @text;
    }

    .plainlinks,
    .archwiki-template-man,
