    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    :root {
      color-scheme: if(@lookup = latte, light, dark);
    }

    html[data-darkreader-scheme="dark"] body::before {
      content: "Please turn off DarkReader for this domain when Catppuccin for NixOS Wiki is active.";
      background: @red;
      box-shadow:
        0 4px 16px fade(@crust, 10%),
        0 8px 32px fade(@crust, 5%);
      color: @crust;
      position: fixed;
      top: 0;
      right: 0;
      z-index: 99999;
      max-width: 24rem;
      padding: 0.75rem;
      margin: 0.5rem;
      border-radius: 5px;
    }

    body {
      background-color: @base;
    }
    hr {
      border-color: @surface2;
    }
    .navbar-default {
      color: @text;
      background-color: @mantle !important;
      background-image: none !important;
      border-color: transparent !important;

      .navbar-brand {
        color: @blue;
        &:hover {
          color: @sky;
        }
      }

      li {
        a {
          color: @text !important;
          &:hover {
            color: @subtext0 !important;
          }
        }
        &.nav.open > a,
        ul {
          background-color: @surface0 !important;
          background-image: none !important;
          a:hover {
            background-color: fade(@surface1, 50%) !important;
            background-image: none !important;
          }
        }
      }
      .active > a {
        color: @crust !important;
        &:hover {
          color: @base !important;
        }
        background: @blue !important;
      }
    }

    .search-query.form-control {
      color: @text;
      background-color: @surface0;
      border-color: @surface0;
    }

    * {
      color: @text;
    }

    a {
      color: @blue;
      &:hover {
        color: @sky;
      }
      &.new {
        color: @red;
        &:hover {
          color: @maroon;
        }
      }
    }

    .panel {
      background-color: @mantle;
      border-color: @mantle;
      .panel-heading {
        color: @text;
        background-color: @surface0 !important;
        border-bottom-color: @surface2;
        img {
          & when not (@lookup = latte) {
            filter: invert(100%);
          }
        }
      }
    }

    .btn-primary {
      color: @crust;
      background-color: @blue !important;
      background-image: none !important;
      border-color: @crust !important;
      &:hover {
        color: @crust !important;
        background-color: @sky !important;
        background-image: none !important;
      }
    }

    table {
      &,
      * {
        color: @text;
        border-color: @overlay0 !important;
        background-color: @mantle;
      }
      th {
        &,
        * {
          background-color: @surface0 !important;
        }
      }
    }

    blockquote,
    cite {
      border-left-color: @pink !important;
    }

    cite a {
      color: @pink;
      &:hover {
        color: @rosewater;
      }
    }

    pre,
    code {
      box-shadow: none !important;
      background-color: @mantle;
      border-color: @crust;
    }
    pre {
      color: @text;
    }
    code {
      color: @rosewater;
    }

    *[style*="background-color: #C1E5FF"] {
      color: @text !important;
      background-color: fade(@blue, 15%) !important;
      border-color: @blue !important;
    }

    *[style*="background-color: #FFD0B7"] {
      color: @text !important;
      background-color: fade(@peach, 15%) !important;
      border-color: @peach !important;
    }

    *[style*="background: #F6F6F6"] {
      color: @text !important;
      background-color: fade(@overlay0, 15%) !important;
      border-color: @overlay0 !important;
    }

    .mw-highlight,
    .mw-highlight > pre {
      color: @text !important;
      background-color: @mantle !important;

      // env assignment
      .nv {
        color: @text;
      }
      // dollar sign
      .gp {
        color: @pink;
      }
      // comment
      .c1,
      .cm {
        color: @overlay2;
      }
      // strings
      .s1,
      .s2,
      .se {
        color: @green;
      }
      // string interpolation
      .si {
        color: @pink;
      }
      // lhe
      .ss,
      .nb {
        color: @blue;
      }
      // operators
      .p,
      .o {
        color: @teal;
      }
      // numbers, boolean
      .mi,
      .no {
        color: @peach;
      }
      // keyword
      .k,
      .ow {
        color: @mauve;
      }
      // shebangs
      .ch {
        color: @pink;
      }
      // etc
      .go {
        color: @text;
      }
      // diff added
      .gi {
        color: @green;
      }
      // diff removed
      .gd {
        color: @red;
      }
      // diff header
      .gh {
        color: @text;
      }
      // diff changes
      .gu {
        color: @peach;
      }
    }

    .suggestions {
      .suggestions-result {
        background-color: @mantle;
        &:hover {
          background-color: mix(@base, @mantle, 50%);
        }
        color: @text;
        .highlight {
          color: @accent-color;
        }
      }
      .suggestions-results {
        border-color: @surface0 !important;
      }
      .suggestions-special {
        border-color: @surface0 !important;
        background-color: @mantle;
        &:hover {
          background-color: mix(@accent-color, @mantle, 15%);
        }
        .special-label {
          color: @subtext0;
        }
        .special-query {
          color: @text;
        }
      }
    }

    .toc {
      li.active > a {
        border-left-color: @accent-color !important;
      }
      ul.nav a:hover {
        background: @accent-color !important;
        &,
        * {
          color: @crust !important;
        }
      }
    }

    .page-header {
      border-bottom-color: @overlay2;
    }

    #catlinks {
      background-color: @mantle;
      border-color: @mantle;
    }

    #footer {
      background-color: @mantle;
    }
  }

  @media (prefers-color-scheme: light) {
    #catppuccin(@lightFlavor, @accentColor);
  }
  @media (prefers-color-scheme: dark) {
    #catppuccin(@darkFlavor, @accentColor);
  }

  // prettier-ignore
  @catppuccin: {
    @latte:     { @rosewater: #dc8a78; @flamingo: #dd7878; @pink: #ea76cb; @mauve: #8839ef; @red: #d20f39; @maroon: #e64553; @peach: #fe640b; @yellow: #df8e1d; @green: #40a02b; @teal: #179299; @sky: #04a5e5; @sapphire: #209fb5; @blue: #1e66f5; @lavender: #7287fd; @text: #4c4f69; @subtext1: #5c5f77; @subtext0: #6c6f85; @overlay2: #7c7f93; @overlay1: #8c8fa1; @overlay0: #9ca0b0; @surface2: #acb0be; @surface1: #bcc0cc; @surface0: #ccd0da; @base: #eff1f5; @mantle: #e6e9ef; @crust: #dce0e8; }
    @frappe:    { @rosewater: #f2d5cf; @flamingo: #eebebe; @pink: #f4b8e4; @mauve: #ca9ee6; @red: #e78284; @maroon: #ea999c; @peach: #ef9f76; @yellow: #e5c890; @green: #a6d189; @teal: #81c8be; @sky: #99d1db; @sapphire: #85c1dc; @blue: #8caaee; @lavender: #babbf1; @text: #c6d0f5; @subtext1: #b5bfe2; @subtext0: #a5adce; @overlay2: #949cbb; @overlay1: #838ba7; @overlay0: #737994; @surface2: #626880; @surface1: #51576d; @surface0: #414559; @base: #303446; @mantle: #292c3c; @crust: #232634; }
    @macchiato: { @rosewater: #f4dbd6; @flamingo: #f0c6c6; @pink: #f5bde6; @mauve: #c6a0f6; @red: #ed8796; @maroon: #ee99a0; @peach: #f5a97f; @yellow: #eed49f; @green: #a6da95; @teal: #8bd5ca; @sky: #91d7e3; @sapphire: #7dc4e4; @blue: #8aadf4; @lavender: #b7bdf8; @text: #cad3f5; @subtext1: #b8c0e0; @subtext0: #a5adcb; @overlay2: #939ab7; @overlay1: #8087a2; @overlay0: #6e738d; @surface2: #5b6078; @surface1: #494d64; @surface0: #363a4f; @base: #24273a; @mantle: #1e2030; @crust: #181926; }
    @mocha:     { @rosewater: #f5e0dc; @flamingo: #f2cdcd; @pink: #f5c2e7; @mauve: #cba6f7; @red: #f38ba8; @maroon: #eba0ac; @peach: #fab387; @yellow: #f9e2af; @green: #a6e3a1; @teal: #94e2d5; @sky: #89dceb; @sapphire: #74c7ec; @blue: #89b4fa; @lavender: #b4befe; @text: #cdd6f4; @subtext1: #bac2de; @subtext0: #a6adc8; @overlay2: #9399b2; @overlay1: #7f849c; @overlay0: #6c7086; @surface2: #585b70; @surface1: #45475a; @surface0: #313244; @base: #1e1e2e; @mantle: #181825; @crust: #11111b; }
  }
}
// vim:ft=less

/* ==UserStyle==
@name npm Catppuccin
@namespace github.com/catppuccin/userstyles/styles/npm
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/npm
@version 0.0.4
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/npm/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Anpm
@description Soothing pastel theme for npm
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire*", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@var select darkFlavor "mocha:Mocha";
@-moz-document domain("www.npmjs.com") {
  /* prettier-ignore */
  @catppuccin: {
    @latte:     { @rosewater: #dc8a78; @flamingo: #dd7878; @pink: #ea76cb; @mauve: #8839ef; @red: #d20f39; @maroon: #e64553; @peach: #fe640b; @yellow: #df8e1d; @green: #40a02b; @teal: #179299; @sky: #04a5e5; @sapphire: #209fb5; @blue: #1e66f5; @lavender: #7287fd; @text: #4c4f69; @subtext1: #5c5f77; @subtext0: #6c6f85; @overlay2: #7c7f93; @overlay1: #8c8fa1; @overlay0: #9ca0b0; @surface2: #acb0be; @surface1: #bcc0cc; @surface0: #ccd0da; @base: #eff1f5; @mantle: #e6e9ef; @crust: #dce0e8; };
    @frappe:    { @rosewater: #f2d5cf; @flamingo: #eebebe; @pink: #f4b8e4; @mauve: #ca9ee6; @red: #e78284; @maroon: #ea999c; @peach: #ef9f76; @yellow: #e5c890; @green: #a6d189; @teal: #81c8be; @sky: #99d1db; @sapphire: #85c1dc; @blue: #8caaee; @lavender: #babbf1; @text: #c6d0f5; @subtext1: #b5bfe2; @subtext0: #a5adce; @overlay2: #949cbb; @overlay1: #838ba7; @overlay0: #737994; @surface2: #626880; @surface1: #51576d; @surface0: #414559; @base: #303446; @mantle: #292c3c; @crust: #232634; };
    @macchiato: { @rosewater: #f4dbd6; @flamingo: #f0c6c6; @pink: #f5bde6; @mauve: #c6a0f6; @red: #ed8796; @maroon: #ee99a0; @peach: #f5a97f; @yellow: #eed49f; @green: #a6da95; @teal: #8bd5ca; @sky: #91d7e3; @sapphire: #7dc4e4; @blue: #8aadf4; @lavender: #b7bdf8; @text: #cad3f5; @subtext1: #b8c0e0; @subtext0: #a5adcb; @overlay2: #939ab7; @overlay1: #8087a2; @overlay0: #6e738d; @surface2: #5b6078; @surface1: #494d64; @surface0: #363a4f; @base: #24273a; @mantle: #1e2030; @crust: #181926; };
    @mocha:     { @rosewater: #f5e0dc; @flamingo: #f2cdcd; @pink: #f5c2e7; @mauve: #cba6f7; @red: #f38ba8; @maroon: #eba0ac; @peach: #fab387; @yellow: #f9e2af; @green: #a6e3a1; @teal: #94e2d5; @sky: #89dceb; @sapphire: #74c7ec; @blue: #89b4fa; @lavender: #b4befe; @text: #cdd6f4; @subtext1: #bac2de; @subtext0: #a6adc8; @overlay2: #9399b2; @overlay1: #7f849c; @overlay0: #6c7086; @surface2: #585b70; @surface1: #45475a; @surface0: #313244; @base: #1e1e2e; @mantle: #181825; @crust: #11111b; };
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    @npmGradient: linear-gradient(139deg, @peach, @maroon, @red, @pink);
    --color-fg-default: @text;
    --color-fg-muted: @subtext1;
    --color-fg-subtle: @subtext0;
    --color-fg-on-emphasis: @base;
    --color-fg-brand: @red;
    --color-fg-accent: @blue;
    --color-fg-success: @green;
    --color-fg-attention: @yellow;
    --color-fg-danger: @red;
    --color-border-strong: @surface0;

    &,
    main {
      background-color: @base !important;
    }

    color-scheme: if(@lookup=latte, light, dark);
    accent-color: @accent-color;

    /* Primary background */
    .bg-white,
    ._4ea0e50d {
      background-color: @base;
    }

    /* Background layer background */
    ._7eb68a55,
    .e9998f88 {
      background-color: @mantle;
    }

    /* Primary text */
    ._99e3854f,
    .black,
    .black-90,
    .black-80,
    ._0b9b6c57,
    ._1b8c0a9f,
    p,
    a {
      color: @text;
    }

    /* Secondary text */
    .black-70,
    .black-60,
    .cd2827bb a {
      color: @subtext0;
    }
    ._66c2abad {
      color: @overlay0;
    }

    /* Inverse text */
    .white {
      color: @base;
    }

    /* Border colors */
    .b--black-10,
    .b--black-20 {
      border-color: @surface0;
    }

    /* --- ARBITRARY --- */

    /* Top loading bar */
    div.fixed.top-0.left-0.z-999 {
      background-color: @red;
    }

    /* Magnifying glass icon (search box, discover packages) */
    svg g[stroke="#777777"] {
      stroke: @subtext0;
    }

    /* "Beta" pill */
    .a17280e0 {
      color: @green;
      border-color: @green;
    }
    /* Green checkmark */
    [fill="#107010"] {
      fill: @green;
    }

    /* --- HEADER --- */
    [data-test-id="notification-banner"] {
      background-color: if(
        @lookup = latte,
        desaturate(lighten(@green, 30%), 5%),
        desaturate(darken(@green, 5%), 5%)
      );
      border-color: @green;

      #notification,
      button[aria-label="Close notification"] {
        color: if(@lookup = latte, @text, @base);
      }
    }
    header {
      border-bottom-color: @surface0;

      /* Top bar gradient */
      .e7070742 {
        border-image: @npmGradient 3;
      }

      /* Heart icon */
      ._0edb515f {
        color: @text;
      }

      /* Logo */
      a[href="/"] > svg > path {
        fill: @text;
      }

      /* Search box */
      form#search {
        button[type="submit"] {
          background-color: @subtext1;
        }

        & > div {
          border-color: @crust;

          &:focus-within {
            border-color: @subtext1;
          }

          & > div {
            background-color: @crust;

            input[type="search"] {
              color: @text;

              &::placeholder {
                color: @overlay1;
              }

              &:focus {
                box-shadow: none;
              }

              &::-webkit-search-cancel-button {
                display: none;
              }
            }
          }

          /* Floating live results */
          ul li {
            border-bottom-color: @surface0;
            color: @text;
          }
        }
      }

      /* Profile picture nvigation */
      #signup {
        border-color: @surface0;
      }
      nav > button[aria-label="Profile menu"] {
        /* Dropdown arrow */
        img[alt="avatar"] {
          border-color: @text;

          & + div > svg {
            fill: @text;
          }
        }

        /* Popup */
        + span > div {
          border-color: @surface0;

          /* Popup triangle */
          &::after {
            border-bottom-color: @base;
          }
          &::before {
            border-bottom-color: @surface0;
          }

          /* Username */
          h2 {
            border-bottom-color: @surface1;
          }

          /* Navigation options */
          ul li a {
            &:hover,
            &:focus {
              background-color: @crust;
            }

            /* Red "Add Organization" button */
            &[href="/org/create"] {
              border-color: @surface0;
              color: @red;
              svg {
                fill: @red;
              }
            }
          }
        }
      }
    }

    /* --- HOME PAGE --- */

    article > section:nth-of-type(1) {
      background-image: linear-gradient(
          270deg,
          fade(@red, 16%) 0%,
          fade(@red, 56%) 18.45%,
          fade(@red, 80%) 49.67%,
          fade(@red, 56%) 82.52%,
          fade(@red, 19%) 99.7%,
          fade(@red, 0%) 99.71%,
          fade(@red, 0%) 99.72%,
          fade(@red, 16%) 99.73%
        ),
        url("https://static-production.npmjs.com/abf53a31b2da4657a1a004ee9358551c.png");

      a[href="/signup"] {
        background-color: @yellow;
      }
      a[href="/products/pro"] {
        background-color: @red;
        border-color: @text;
      }
    }
    article > section:nth-of-type(2) {
      color: @text;
    }

    /* Popular libraries section */
    #popular-libraries-header + hr {
      border-color: @red;
    }
    ul[aria-labelledby="popular-libraries-header"] {
      li a {
        border-bottom-color: @surface0;
      }
    }

    /* Discover packages section */
    #discover-packages-header + hr {
      border-color: mix(@red, @yellow);
    }
    ul[aria-labelledby="discover-packages-header"] li {
      a {
        color: @text;
        border-color: @surface0;
      }
      &:nth-of-type(6n + 1):hover a {
        color: @mauve;
        border-bottom-color: @mauve;
      }
      &:nth-of-type(6n + 2):hover a {
        color: @green;
        border-bottom-color: @green;
      }
      &:nth-of-type(6n + 3):hover a {
        color: @sky;
        border-bottom-color: @sky;
      }
      &:nth-of-type(6n + 4):hover a {
        color: @red;
        border-bottom-color: @red;
      }
      &:nth-of-type(6n + 5):hover a {
        color: @yellow;
        border-bottom-color: @yellow;
      }
      &:nth-of-type(6n + 6):hover a {
        color: @pink;
        border-bottom-color: @pink;
      }
    }

    /* By the numbers section */
    ._8f26e3fd {
      border-color: @yellow;
    }

    /* Recently updated packages section */
    #recently-updated-packages-header + hr {
      border-color: @red;
    }

    /* --- PACKAGE PAGES --- */

    /* "public" text */
    ._813b53b2 {
      color: @green;
    }
    /* TypeScript logo */
