/* ==UserStyle==
@name Learn You A Haskell Catppuccin
@namespace github.com/catppuccin/userstyles/styles/learn-you-a-haskell
@version 0.0.1
@description Soothing pastel theme for Learn You A Haskell

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire*", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */
/* If you need any help with the information above, please see: https://github.com/openstyles/stylus/wiki/Writing-UserCSS#var */
/* For help with URL rules, please see https://github.com/openstyles/stylus/wiki/Writing-styles#valid--moz-document-rules. */
/*
@var select darkFlavor "mocha:Mocha";
@var select darkFlavor "latte:Latte";
@var select darkFlavor "frappe:Frappé";
@var select darkFlavor "macchiato:Macchiato";
*/
@-moz-document domain("learnyouahaskell.com") {
    #catppuccin(@lookup, @accent) {
        @rosewater: @catppuccin[@@lookup][@rosewater];
        @flamingo: @catppuccin[@@lookup][@flamingo];
        @pink: @catppuccin[@@lookup][@pink];
        @mauve: @catppuccin[@@lookup][@mauve];
        @red: @catppuccin[@@lookup][@red];
        @maroon: @catppuccin[@@lookup][@maroon];
        @peach: @catppuccin[@@lookup][@peach];
        @yellow: @catppuccin[@@lookup][@yellow];
        @green: @catppuccin[@@lookup][@green];
        @teal: @catppuccin[@@lookup][@teal];
        @sky: @catppuccin[@@lookup][@sky];
        @sapphire: @catppuccin[@@lookup][@sapphire];
        @blue: @catppuccin[@@lookup][@blue];
        @lavender: @catppuccin[@@lookup][@lavender];
        @text: @catppuccin[@@lookup][@text];
        @subtext1: @catppuccin[@@lookup][@subtext1];
        @subtext0: @catppuccin[@@lookup][@subtext0];
        @overlay2: @catppuccin[@@lookup][@overlay2];
        @overlay1: @catppuccin[@@lookup][@overlay1];
        @overlay0: @catppuccin[@@lookup][@overlay0];
        @surface2: @catppuccin[@@lookup][@surface2];
        @surface1: @catppuccin[@@lookup][@surface1];
        @surface0: @catppuccin[@@lookup][@surface0];
        @base: @catppuccin[@@lookup][@base];
        @mantle: @catppuccin[@@lookup][@mantle];
        @crust: @catppuccin[@@lookup][@crust];
        @accent-color: @catppuccin[@@lookup][@@accent];

        @import url('https://fonts.googleapis.com/css2?family=Kode+Mono:wght@400..700&family=Ojuju:wght@200..800&family=Open+Sans:ital,wght@0,300..800;1,300..800&family=Space+Grotesk:wght@300..700&display=swap');

        /* Start styling your website here: */
        body {
            background-color: @base;
            color: @text;
            font-family: "Open Sans";
        }

        html, body, .bgwrapper, #content {
            width: 100%;
            overflow-x: hidden;
        }

        h1,h2,h3,h4,h5,h6 {
            font-family: "Space Grotesk";
        }

        h1 {
            font-size: 2rem;
        }
        h2 {
            font-size: 1.6rem;
        }
        h3 {
            font-size: 1.4rem;
        }
        h4 {
            font-size: 1.25rem;
        }
        h5 {
            font-size: 1.15rem;
        }
        h6 {
            font-size: 1.1rem;
        }
        p {
            font-size: 1rem;
        }

        a {
            color: @accent-color;
            font-style: bold;
            text-decoration: underline;
        }

        .hintbox {
            width: 80%;
            max-width: 60ch;
            border: 1.5px solid @accent-color;
            border-radius: 0.25rem;
            padding: 1rem;
            position: relative;
            &:after {
                position: absolute;
                width: 100%;
                height: 100%;
                inset: 0;
                border-radius: 0.25rem;
                box-shadow: 0px 0px 10px 5px @accent-color;
                background-color: @surface2;
                opacity: .125;
                content: '';
                pointer-events: none;
            }
        }

        & when (@lookup =mocha) {
            pre[name="code"] {
                color: @pink;
                background-color: @surface0;
            }
            span.fixed {
                color: @green;
                background-color: @surface0;
            }
        }
        & when (@lookup =latte) {
            pre[name="code"] {
                color: @lavender;
                background-color: @crust;
            }
            span.fixed {
                color: @red;
                background-color: @crust;
            }
        }
        & when (@lookup =frappe) {
            pre[name="code"] {
                color: @pink;
                background-color: @crust;
            }
            span.fixed {
                color: @green;
                background-color: @crust;
            }
        }
        & when (@lookup =macchiato) {
            pre[name="code"] {
                color: @pink;
                background-color: @surface0;
            }
            span.fixed {
                color: @green;
                background-color: @surface0;
            }
        }

        span.fixed, pre[name="code"] {
            border-radius: 0.15rem;
            padding: 0 0.25rem;
            font-family: "Kode Mono";
            font-size: .85rem;
        }
        pre[name="code"] {
            max-width: 60ch;
            width: 80%;
            padding: 1rem 1rem;
            overflow: auto;
        }

        .footdiv {
            width: 100%;
            display: flex;
            flex-direction: column;
            align-items: center;
            ul {
                display: flex;
                flex-direction: column;
                align-items: start;
                justify-content: center;
                gap: 0.5rem;
                width: fit-content;
                li {
                    list-style: none;
                    font-size: 1rem;
                }
                li:nth-child(1) {
                    display: hidden;
                }
            }
        }

        :root {
            font-size: 16px;        
        }

        p {
            max-width: 60ch;
            width: 80%;
            margin-bottom: 1rem;
        }

        #content {
            display: flex;
            flex-direction: column;
            align-items: center;
        }

        img {
            display: block;
            margin: 2.5rem auto;
            width: 90%;
            max-width: 14rem;
            height: auto;
        }

        ol.chapters li {
            list-style: none;
            ul {
                margin-left: 2rem;
            }
        }
    }


    /* This section applies the light/dark themes automatically based on the user's system theme.
     Uncomment this *only* if the website you are theming doesn't have a light/dark mode toggle.
     For an actual example of this, see https://github.com/catppuccin/userstyles/blob/39288b593278e5efa48007d5fc001292cf910672/styles/brave-search/catppuccin.user.css#L16-L29. */
  @media (prefers-color-scheme: light) {
    :root {
      #catppuccin(@lightFlavor, @accentColor);
    }
  }
  @media (prefers-color-scheme: dark) {
    :root {
      #catppuccin(@darkFlavor, @accentColor);
    }
  }
 /* */
    /* If the website you are theming *does* have a light/dark mode toggle,
     you can conditionally enable light/dark flavors by targeting whatever attribute that website uses to indicate the theme.
     Un-comment this block if that is the case.
     For an actual example of this, see https://github.com/catppuccin/userstyles/blob/39288b593278e5efa48007d5fc001292cf910672/styles/youtube/catppuccin.user.css#L21-L30.
  :root[data-theme="dark"] {
    #catppuccin(@darkFlavor, @accentColor);
  }
  :root[data-theme="light"] {
    #catppuccin(@lightFlavor, @accentColor);
  }
  */
}

